import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  const [password,setPassword]=useState("");
  const [passwordLength,setpasswordlength]=useState(12);
  const [useSymbols, setUseSymbols] = useState(true);
  const [useNumbers, setUseNumbers] = useState(true);
  const [useLowerCase, setUseLowerCase] = useState(true);
  const [useUpperCase, setUseUpperCase] = useState(true);
  const [successMessage, setSuccessMessage] = useState("");

  const generatePassword = ()=>{
    let charset="";
    let newPassword="";
    if (useSymbols) charset += "!@#$%^&*()";
        if (useNumbers) charset += "0123456789";
        if (useLowerCase) charset += "abcdefghijklmnopqrstuvwxyz";
        if (useUpperCase) charset += "ABCDEFGHIJKLMNOPQRSTUVWXYZ";

        for (let i = 0; i < passwordLength; i++) {
            newPassword += charset.charAt(Math.floor(Math.random() * charset.length));
        }

        setPassword(newPassword);
  }


  return (
    <div className="box">
      <h1>random password generator</h1>
      <div className="inputContainer">

        <input type="number" min='8' max='32' onChange={(e)=>setpasswordlength(e.target.value)}/>
        <div className="generate">
          <button onClick={generatePassword}>generate password</button>
        </div>
       <div className="checkboxcont">
         <input type='checkbox' checked={useSymbols} onChange={()=>setUseSymbols(!useSymbols)}/>symbols
         <input type='checkbox' checked={useNumbers} onChange={()=>setUseNumbers(!useNumbers)}/>numbers
         <input type='checkbox' checked={useLowerCase} onChange={()=>setUseLowerCase(!useLowerCase)}/>
         <input type='checkbox' checked={useUpperCase} onChange={()=>setUseUpperCase(!useUpperCase)}/>
       </div>

      </div>
    </div>
  );
}

export default App;
